// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ScholarChain {
    enum Role { None, Author, Faculty, Reviewer }

    struct User {
        Role role;
        string name;
    }

    struct Paper {
        uint256 paperId;
        address author;
        string title;
        string ipfsHash;
        uint256 timestamp;
        string[] reviews;
    }

    address public owner;
    uint256 public paperCount;

    mapping(address => User) public users;
    mapping(uint256 => Paper) public papers;

    event UserRegistered(address indexed user, string name, Role role);
    event PaperSubmitted(uint256 indexed paperId, address indexed author, string title, string ipfsHash);
    event ReviewAdded(uint256 indexed paperId, address indexed reviewer, string comment);

    constructor() {
        owner = msg.sender;
    }

    modifier onlyRegistered() {
        require(users[msg.sender].role != Role.None, "User not registered");
        _;
    }

    function register(address _user, string memory _name, uint8 _role) public {
        require(_user != address(0), "Invalid address");
        require(users[_user].role == Role.None, "User already registered");
        require(_role > 0 && _role <= uint8(Role.Reviewer), "Invalid role");

        users[_user] = User(Role(_role), _name);
        emit UserRegistered(_user, _name, Role(_role));
    }

    function submitPaper(string memory _title, string memory _ipfsHash) public onlyRegistered {
        require(users[msg.sender].role == Role.Author, "Only authors can submit");

        paperCount++;
        papers[paperCount] = Paper({
            paperId: paperCount,
            author: msg.sender,
            title: _title,
            ipfsHash: _ipfsHash,
            timestamp: block.timestamp,
            reviews: new string[](0) 
        });

        emit PaperSubmitted(paperCount, msg.sender, _title, _ipfsHash);
    }

    function addReview(uint256 _paperId, string memory _comment) public onlyRegistered {
        require(_paperId > 0 && _paperId <= paperCount, "Invalid paper ID");
        Role r = users[msg.sender].role;
        require(r == Role.Faculty || r == Role.Reviewer, "Only faculty/reviewer can review");

        papers[_paperId].reviews.push(_comment);
        emit ReviewAdded(_paperId, msg.sender, _comment);
    }

    function getAllPapers() public view returns (Paper[] memory) {
        Paper[] memory list = new Paper[](paperCount);
        for (uint256 i = 1; i <= paperCount; i++) {
            list[i - 1] = papers[i];
        }
        return list;
    }

    function getPaper(uint256 _paperId) public view returns (
        uint256,
        address,
        string memory,
        string memory,
        uint256,
        string[] memory
    ) {
        require(_paperId > 0 && _paperId <= paperCount, "Invalid paper ID");
        Paper storage p = papers[_paperId];
        return (p.paperId, p.author, p.title, p.ipfsHash, p.timestamp, p.reviews);
    }
}
